(()=>{"use strict";const t=new class{constructor(t){this.time=t,this._interval=null}display(){let t=Math.floor(this.time/3600),e=Math.floor(this.time/60%60),n=Math.floor(this.time%60);n<10&&(n="0"+n),e<10&&(e="0"+e),t<10&&(t="0"+t),document.getElementById("timer").innerHTML=`${t} : ${e} : ${n}`}countDown(){this.time-=1,this.display()}start(){this._interval=setInterval((()=>this.countDown()),1e3)}stop(){clearInterval(this._interval),this._interval=null}backFive(){this.time+=300,this.display()}skipFive(){this.time-=300,this.display()}}(28800);t.display();const e=document.createElement("button");e.innerHTML="Start",e.addEventListener("click",t.start),document.body.appendChild(e);const n=document.createElement("button");n.innerHTML="Pause",n.addEventListener("click",t.stop),document.body.appendChild(n);const i=document.createElement("button");i.innerHTML="Back 5 Mins",i.addEventListener("click",t.backFive),document.body.appendChild(i);const s=new class{constructor(){this.count=100,this.exerciseList=["Chin Ups","Push Ups","Sit Ups"],this.counter=document.createElement("div")}createDropdown(){const t=document.createElement("select");t.id="selection",document.body.appendChild(t);for(let e in this.exerciseList){const n=document.createElement("option");n.value=this.exerciseList[e],n.text=this.exerciseList[e],t.appendChild(n)}}createCounter(){document.body.appendChild(this.counter),this.counter.innerHTML=this.count}display(){this.counter.innerHTML=this.count}doneTen(){this.count-=10,this.display()}};s.createDropdown(),s.createCounter(),s.display();let c=document.createElement("button");c.innerHTML="One",c.addEventListener("click",s.doneOne),document.body.appendChild(c);let o=document.createElement("button");o.innerHTML="Ten",o.addEventListener("click",s.doneTen),document.body.appendChild(o)})();